#define PI 3.1416
#define TWO_PI 2.0*PI;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5, 0.5);

    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	vec2 center = vec2(0.0, 0.0);
    float dist = length(uv - center);
    float radius = 0.1;
    vec2 mdist = abs(uv - center) - radius;
    float sqmdist = max(mdist.x, mdist.y);
    float angle = 0.3 * atan(uv.y, uv.x) / TWO_PI;
    float vangle = mod(angle, 1.0);
    float mi = mix(sqmdist, dist, vangle);
    float c = pow(- mi + 1.0 + radius, 5.0);
    vec3 mod = vec3(0.265, 0.34, 0.87);
    //vec3 mod = vec3(1.0);
    vec3 col = mod * c;
    // Output to screen
    fragColor = vec4(col,1.0);
}
