#define S(v)  smoothstep( 2./R.y, 0., v ) 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         P = ( u - .5* R ) / R.y;
         P += sin(iTime * 5. + P.yx * 25.) * .01;

    float l = length(P),
          a = S( l - .15 ),
          b = S( l - .25 );

    O = vec4( mix(a,1.-a, b) );
}


