void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    Q =abs(vec4(a.ywww));
}

void mainImage( out vec4 Q, in vec2 U )
{
        Q = vec4(0);
        vec2 w = 2.*pi*(U-0.5)/R;
        for (float i = 0.; i < R.y; i++) {
            vec4 a = A(vec2(U.x,i));
            Q.xy += a.xy*e(w.y*i)/R.y;
            Q.zw += a.zw*e(w.y*i)/R.y;
        }
        Q *= mod(U.x+U.y,2.)*2.-1.;
    vec2 u=U;
    u -= 0.25*B(u).xz;
    u -= 0.25*B(u).xz;
    u -= 0.25*B(u).xz;
    u -= 0.25*B(u).xz;
    Q.yw = B(u).yw;
    if (length(U-iMouse.xy)<10.&&iMouse.z>0.) Q =vec4(5,sin(.37*iTime),0,cos(iTime));
    if (iFrame < 1) {
        Q = vec4(0);
        if (length(U-0.5*R)<30.) Q.zyw = vec3(10,1,1);
    }
    if (U.x<5.||U.y<5.||R.x-U.x<5.||R.y-U.y<5.)Q.xz*=0.;
}

#define R (iResolution.xy)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texelFetch(iChannel2,ivec2(U),0)
#define D(U) texelFetch(iChannel3,ivec2(U),0)
#define pi 3.14159265359
mat2 e (float t) {
    float c = cos(t),s = sin(t);
	return mat2(c,-s,s,c);
}

vec4 T(vec2 U) {return B(U-B(U).xz+vec2(0.5,0));}
void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        vec4 a = T(vec2(i,U.y));
        Q.xy += vec2(a.x,0)*e(-(w.x-pi)*i);
        Q.zw += vec2(a.z,0)*e(-(w.x-pi)*i);
    }
}

void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R-pi;
    for (float i = 0.; i < R.y; i++) {
        vec4 a = A(vec2(U.x,i));
        Q.xy += a.xy*e(-(w.y)*i);
        Q.zw += a.zw*e(-(w.y)*i);
    }
    if (dot(w,w)>0.) {
    	Q.xz -= w*dot(Q.xz,w)/dot(w,w);
    	Q.yw -= w*dot(Q.yw,w)/dot(w,w);
    }
}

void mainImage( out vec4 Q, in vec2 U )
{
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        vec4 a = A(vec2(i,U.y));
        Q.xy += a.xy*e(w.x*i)/R.x;
        Q.zw += a.zw*e(w.x*i)/R.x;
    }
}
