vec3 Rect(vec2 cord, float len, float wid)
{

	if(abs(tan(cord.x)) <= len/wid)
        if (cord.y <= wid/abs(cos(cord.x)))
            return vec3(1., 1., 0.);
        else return vec3(.54, .6, 1.);
    else
       if (cord.y <= len/abs(sin(cord.x)))
           return vec3(.5, 1., 0.);
        else return vec3(.6, .75, 1.);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //uv -= vec2(.62, .59);
    uv *= 5.;
    uv.x *= iResolution.x/iResolution.y;

    vec2 new = fract(uv);
    new -= .5;

    vec2 polar = vec2(atan(new.x, new.y), length(new));
    polar.y*= 2.*polar.y;

    vec2 copy = vec2(atan(new.x, new.y), length(new));
    new = vec2(polar.x*2. + iTime, polar.y);

    vec3 figure = Rect(new, .3, .3)
        		 - Rect(new, .2, .2);

    //vec3 col = vec3(figure);
    // Output to screen
    fragColor = vec4(figure,1.0);
}
