float Band(float t,float start,float end,float blur){
    float step1 = smoothstep(start-blur,start+blur,t);
    float step2 = smoothstep(end+blur,end-blur,t);
    return step1*step2;

}

float Rect(vec2 uv,float top,float bottom,float left,float right,float blur){

    float horizontal = Band(uv.x,left,right,blur);
    float vertical = Band(uv.y,bottom,top,blur);
    return horizontal*vertical;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
	uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    vec3 col = vec3(1.,1.,1.);

  	float x = uv.x;
    float y = uv.y;
    y += cos(x*10.+iTime*4.)*0.1;
    x += sin(y*10.+iTime*4.)*0.1;

    float c = Rect(vec2(x,y),0.1,-0.1,-0.7,0.7,0.005);



    fragColor = vec4(col,1.0)*c;
}
