vec3 Circle(vec2 uv, vec2 position, float radius, float blurAmount, vec3 color){

    float distance = length(uv - position);

    float result = smoothstep(radius, radius - blurAmount, distance);

    return result * color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;


    ////Vertical Movement
    float walkingSpeed = 2.;
    float yOffset = .9;
    float yStepHeight = 2.;

    float yMovement = cos( cos(iTime * walkingSpeed) / yStepHeight);
    yMovement -= yOffset;


    ////Horitontal Movement
    float xMovement = tan(iTime);



    ////Drawing Circles
    //head
    vec3 result = Circle(uv , vec2(.1 - xMovement, .0 - yMovement) , .3 , 0.01, vec3(1.0, 1.0, 1.0));

     //beak
    result += Circle(uv , vec2(-.225 - xMovement, .05 - yMovement) , .1 , 0.01, vec3(1.0, .5, 0.));

     //eye
    result -= Circle(uv , vec2(-.03 - xMovement, .05 - yMovement) , .1 , 0.01, vec3(1., 1., 1.));

     //body
    result += Circle(uv , vec2(.4 - xMovement, -.6 - yMovement) , .5 , 0.01, vec3(1.0, 1.0, 1.0));




    ////Output to screen
    fragColor = vec4(result ,1.);
}
