void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;
	float iswhite = step(abs(uv.x), 0.165);
    fragColor = vec4(step(0.165, uv.x) + iswhite, 
                     iswhite, 
                     step(uv.x, -0.165) + iswhite,
                     1.0);
}
