float circle(float r, in vec2 uv, in vec2 pos){

    uv = uv-pos;
    return 1.0-step(r,length(uv));

}

float rect(in vec2 uv, float a, float b, float c, float d, in vec2 pos){

    uv = (uv+vec2(0.5,0.5))-pos;
    float xy = step(a,uv.x);
    xy *= step(b,uv.y);
    xy *= step(c,1.-uv.x);
    xy *= step(d,1.-uv.y);
    return xy;
}

float tri(in vec2 uv, float n, float tam, in vec2 pos ){

    uv = uv-pos;
    float a = atan(uv.x, uv.y) - 3.14;
    float r = 6.28/n;
    float d = cos(floor(0.5+a/r)*r-a)*length(uv);
    d = 1.0-step(tam,d); 
    return d;
}	

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    //buat yang circle, rectangle, triangle yang atas
    float c0 = circle(0.4, p, vec2(-1.2, 0.45));

    float r0 = rect(p,0.02,0.2,0.1,0.02, vec2(0.1,0.4));

    float x0 = tri(p, 3.0, 0.25, vec2(1.2,0.35));


    //buat yang circle, rectangle, triangle yang bawah
    float c1 = circle(0.4, p, vec2(-1.2, -0.5));
    float c2 = circle(0.35, p, vec2(-1.2, -0.5));

    float r1 = rect(p,0.02,0.2,0.1,0.02, vec2(0.1,-0.6));
    float r2 = rect(p,0.07,0.25,0.15,0.07, vec2(0.1,-0.6));

    float x1 = tri(p, 3.0, 0.25, vec2(1.2,-0.65));
    float x2 = tri(p, 3.0, 0.2, vec2(1.2,-0.65));

    col += vec3(c0)*vec3(0,0.5,1);
    col += vec3(r0)*vec3(0,0.5,1);
    col += vec3(x0)*vec3(0,0.5,1);

    col += vec3(c1)*vec3(0,0.5,1);
    col += vec3(r1)*vec3(0,0.5,1);
    col += vec3(x1)*vec3(0,0.5,1);
    col += vec3(c2)*vec3(-1,-1,-1);
    col += vec3(r2)*vec3(-1,-1,-1);
    col += vec3(x2)*vec3(-1,-1,-1);

	fragColor = vec4(col,1.0);
}
