    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float hash(in vec2 st) {
    return fract(sin(dot(st, vec2(12.9898, 4.1414))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    p.x *= iResolution.x/iResolution.y;
	//p*=smoothstep(0.1, 16.0, iTime) * 32.0;
   	p *= 12.0;

    vec2 fp = floor(p);

    // random line direction
    float dir = -1.0 + hash(fp) + 0.5;
    float t = iTime * hash(fp) * 22.0;

    vec2 m = vec2(
    	t + hash(fp),
        0
    );
    vec2 ip = floor(p + m * dir);

    float c = hash(ip);
    c = step(0.9, c);
    vec3 col =  vec3(c);

    float a = 1.0;

    a = 0.00001;

    // Output to screen
    fragColor = vec4(col + a, 1.0);
}
