//noise effect cur
float noiseld(float value) {

 return cos(value + cos(value * 100.0)  );   

}

//noise effect cur
float noiseldx2(float value) {

 return cos(value + cos(value * 100.0) *10.0);   

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coord = fragCoord / iResolution.xy;
     vec2 uv = 20.0 * (fragCoord - iResolution.xy / 2.0) / min(iResolution.y, iResolution.x);

    vec3 color = vec3(uv.x, noiseldx2(iTime), 1.0);



   //pointeur birth
    float d = distance(coord, iMouse.xy/iResolution.xy) ;
    if (d < 0.12 ) {
        color += vec3(noiseld(d*iTime), noiseld(d*iTime), noiseld(d*iTime));

    }



    //color effect with moving mouse
    color.r= iMouse.x/ iResolution.x;
    color.b= iMouse.y/ iResolution.y;



    fragColor = vec4(color ,1.0);
}
