vec2 lowerLeftHouse = vec2(0.4,0.1);
vec2 lowerLeftChim = vec2(0.4,0.5);
vec2 lowerLeftWind1 = vec2(0.5,0.25);
vec2 lowerLeftWind2 = vec2(1.1,0.25);
vec2 lowerLeftHouseTop = vec2(0.5,0.6);
vec2 lowerLeftDoor = vec2(0.75, 0.1);

vec2 dimensionHouse = vec2(1.0, 0.5);
vec2 dimensionChim = vec2(0.3, 0.45);
vec2 dimensionWind = vec2(0.15);
vec2 dimensionHouseTop = vec2(0.8, 0.15);
vec2 dimensionDoor = vec2(0.25,0.4);

vec3 background = vec3(0.3);
vec3 houseColor = vec3(1.0,0.0,0.0);
vec3 chimColor = vec3(0.8,0.1,0.1);
vec3 windColor = vec3(0.9);
vec3 houseColorTop = vec3(1.0,0.0,0.0);
vec3 doorColor = vec3(0.8, 0.5, 0.0);
vec3 doorKnobColor = vec3(0.0);

vec2 circWindowCenter = vec2(0.88, 0.65);
float circWindowRadius = 0.05;

vec2 doorKnobCenter = vec2(0.93, 0.3);
float doorKnobRadius = 0.02;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    //creates each layer
    vec4 layer0 = vec4(background, 1.0);
    vec4 layer1 = vec4(houseColor, 0.0);
    vec4 layer2 = vec4(chimColor, 0.0);
    vec4 layer3 = vec4(windColor, 0.0);    
    vec4 layer4 = vec4(windColor, 0.0);
    vec4 layer5 = vec4(houseColorTop, 0.0);
    vec4 layer6 = vec4(windColor, 0.0);
    vec4 layer7 = vec4(doorColor, 0.0);
    vec4 layer8 = vec4(doorKnobColor, 0.0);


    //does check if 
	float opacity1 = (step(lowerLeftHouse.x, uv.x) - step((lowerLeftHouse.x + dimensionHouse.x), uv.x)) * 
        			 (step(lowerLeftHouse.y, uv.y) - step((lowerLeftHouse.y + dimensionHouse.y), uv.y));
    	layer1.a = opacity1;

    float opacity2 = (step(lowerLeftChim.x, uv.x) - step((lowerLeftChim.x + dimensionChim.x), uv.x)) * 
        			 (step(lowerLeftChim.y, uv.y) - step((lowerLeftChim.y + dimensionChim.y), uv.y));
    	layer2.a = opacity2;

    float opacity3 = (step(lowerLeftWind1.x, uv.x) - step((lowerLeftWind1.x + dimensionWind.x), uv.x)) * 
        			 (step(lowerLeftWind1.y, uv.y) - step((lowerLeftWind1.y + dimensionWind.y), uv.y));
    	layer3.a = opacity3;

    float opacity4 = (step(lowerLeftWind2.x, uv.x) - step((lowerLeftWind2.x + dimensionWind.x), uv.x)) * 
        			 (step(lowerLeftWind2.y, uv.y) - step((lowerLeftWind2.y + dimensionWind.y), uv.y));
    	layer4.a = opacity4;

    float opacity5 = (step(lowerLeftHouseTop.x, uv.x) - step((lowerLeftHouseTop.x + dimensionHouseTop.x), uv.x)) * 
        			 (step(lowerLeftHouseTop.y, uv.y) - step((lowerLeftHouseTop.y + dimensionHouseTop.y), uv.y));
    	layer5.a = opacity5;

    float opacity6 = step(length(circWindowCenter - uv), circWindowRadius);
    	layer6.a = opacity6;

    float opacity7 = (step(lowerLeftDoor.x, uv.x) - step((lowerLeftDoor.x + dimensionDoor.x), uv.x)) * 
        			 (step(lowerLeftDoor.y, uv.y) - step((lowerLeftDoor.y + dimensionDoor.y), uv.y));
    	layer7.a = opacity7;

    float opacity8 = step(length(doorKnobCenter - uv), doorKnobRadius);
    	layer8.a = opacity8;


    //mixes each layer
    vec4 component = layer0;
    component = mix(layer0, layer2, layer2.a);
    component = mix(component, layer1, layer1.a);
    component = mix(component, layer3, layer3.a);
    component = mix(component, layer4, layer4.a);
    component = mix(component, layer5, layer5.a);
    component = mix(component, layer6, layer6.a);
    component = mix(component, layer7, layer7.a);
    component = mix(component, layer8, layer8.a);

    // Output to screen
    fragColor = component;
}
