// Fork of https://shadertoy.com/view/tslSDl

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U = fract( 8.* U/R.y * mat2(1.155,0,.5,1)); // hexa coords
    vec3 P = vec3(U,fract(U.x-U.y)),            // local hexa coords
         Q = P-.5; 
    float M = max(P.x,max(P.y,P.z)),
          m = min(P.x,min(P.y,P.z));
  //O = vec4( M>.9 && (m>.1||Q.x*Q.y<0.)? vec3(step(M,P)) : m < .1 ? P-P : .5*P/P,1);
  //O = vec4( M>.9 && (m>.1||Q.x*Q.y<0.)? 1. : m < .1 ? 0. : .5);
    O = vec4( M>.9 && (m>.1||Q.x*Q.y<0.)? 1.4-abs(M-.95)/.05 : m < .1 ? .5-.5*(.1-m)/.05 : .5);
}
