const float TIMESCALE = 25.0;
const float TIMEOFFSET = 0.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xx;

    vec2 uvScroll = vec2(
        mod(iTime*TIMESCALE + TIMEOFFSET, iResolution.x)/iResolution.x,
        mod(iTime*TIMESCALE + TIMEOFFSET, iResolution.y)/iResolution.y
    );

    vec2 uvVerticalScroll1 = vec2(
        uv.x,
        pow(uv.y, 1.75) - uvScroll.y * 3.0
    );

	vec2 uvVerticalScroll2 = vec2(
        1.0 - uv.x,
        pow(uv.y, 1.35) - uvScroll.y * 2.0
    );

    vec4 texelSize = vec4( 1.0 / iChannelResolution[0].xy,  iChannelResolution[0].xy);

    float val1 = texture_bicubic(iChannel0, uvVerticalScroll1, texelSize).r;
    float val2 = texture_bicubic(iChannel0, uvVerticalScroll2, texelSize).r;
    float valDiff = 0.5 + val1*0.5 - val2*0.5;
    float vGrad = 1.0 - fragCoord.y/iResolution.y;

    // Darken top
    float fireGrad1 = valDiff*valDiff*vGrad;
    // Brighten bottom
    float fireGrad2 = 1.0 - (1.0 - fireGrad1)*(1.0 - vGrad*vGrad);
    // Ramp shader
    fragColor = texture(iChannel1, vec2(fireGrad2, 0.5));
}

// from: https://www.shadertoy.com/view/4df3Dn

// 4x4 bicubic filter using 4 bilinear texture lookups 
// See GPU Gems 2: "Fast Third-Order Texture Filtering", Sigg & Hadwiger:
// http://http.developer.nvidia.com/GPUGems2/gpugems2_chapter20.html

// w0, w1, w2, and w3 are the four cubic B-spline basis functions
float w0(float a)
{
    return (1.0/6.0)*(a*(a*(-a + 3.0) - 3.0) + 1.0);
}

float w1(float a)
{
    return (1.0/6.0)*(a*a*(3.0*a - 6.0) + 4.0);
}

float w2(float a)
{
    return (1.0/6.0)*(a*(a*(-3.0*a + 3.0) + 3.0) + 1.0);
}

float w3(float a)
{
    return (1.0/6.0)*(a*a*a);
}

// g0 and g1 are the two amplitude functions
float g0(float a)
{
    return w0(a) + w1(a);
}

float g1(float a)
{
    return w2(a) + w3(a);
}

// h0 and h1 are the two offset functions
float h0(float a)
{
    return -1.0 + w1(a) / (w0(a) + w1(a));
}

float h1(float a)
{
    return 1.0 + w3(a) / (w2(a) + w3(a));
}

vec4 texture_bicubic(sampler2D tex, vec2 uv, vec4 texelSize)
{
	uv = uv*texelSize.zw + 0.5;
	vec2 iuv = floor( uv );
	vec2 fuv = fract( uv );

    float g0x = g0(fuv.x);
    float g1x = g1(fuv.x);
    float h0x = h0(fuv.x);
    float h1x = h1(fuv.x);
    float h0y = h0(fuv.y);
    float h1y = h1(fuv.y);

	vec2 p0 = (vec2(iuv.x + h0x, iuv.y + h0y) - 0.5) * texelSize.xy;
	vec2 p1 = (vec2(iuv.x + h1x, iuv.y + h0y) - 0.5) * texelSize.xy;
	vec2 p2 = (vec2(iuv.x + h0x, iuv.y + h1y) - 0.5) * texelSize.xy;
	vec2 p3 = (vec2(iuv.x + h1x, iuv.y + h1y) - 0.5) * texelSize.xy;

    return g0(fuv.y) * (g0x * texture(tex, p0)  +
                        g1x * texture(tex, p1)) +
           g1(fuv.y) * (g0x * texture(tex, p2)  +
                        g1x * texture(tex, p3));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float hGrad = fragCoord.x/iResolution.x;
    float hGrad2 = hGrad*hGrad;

    vec4 color = vec4(
        min(hGrad * 1.25, 1.0),
        (hGrad2*0.9)+0.05,
        (hGrad2*0.5)+0.15,
        1.0
    );

    vec4 mask = vec4(smoothstep(0.1, 0.75, hGrad));

    fragColor = color * mask;
}
