void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 mouse = iMouse.xyz/iResolution.xyz;

    vec2 xy = fragCoord.xy;
    xy.x = xy.x/iResolution.x;
    xy.y = xy.y/iResolution.y;
    vec4 col = vec4(0.0,0.0,0.0,1.0);
    col.r = xy.x;
    col.g = xy.x;
    col.b = xy.x;

    vec2 uv = fragCoord.xy / iResolution.xy;
    float value = uv.x;
    value = 0.5;

    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    vec2 pos = vec2(0.5*ratio, 0.5);


    uv *= 3.0;

    uv = fract(uv);

     uv -= pos ;
    uv += pos;


    //x 
    float valx = uv.x;
    valx = fract(sin(abs(uv.x * 3.0)));
    vec3 mCol2 = vec3(sin(valx * iTime * 4.0) * 0.5,
                     sin(valx * iTime * 8.0) * 0.8, 
                     (sin(valx * iTime) * 6.0) + 0.0);

    //y 
    float valy = uv.y;
    valy = fract(sin(abs(uv.y * 3.0)));
    vec3 mCol3 = vec3(sin(valy * iTime * 4.0) * 0.5,
                     sin(valy * iTime * 8.0) * 0.1, 
                     (sin(valy * iTime) * 6.0) + 0.0);


    fragColor = vec4(mCol2 * mCol3, 1.0);

}
