float Circle(vec2 uv, vec2 pos, float rad, float blur) 
{
	float d = length(uv-pos);
	float t = smoothstep(rad, rad-blur, d);


	return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float a = 0.0;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col= vec3(0.0, 0.0, 0.0);

    float mask = Circle(uv, vec2(0.0, 0.01), 0.3, 0.01);

    mask -= Circle(uv , vec2(-0.13, 0.15), 0.07, 0.01);
    mask -= Circle(uv , vec2(0.13, 0.15), 0.07, 0.01);
    								//HERE change to 0.2
    float mouth = Circle(uv,vec2(0.0, a + clamp(abs(sin(iTime)), 0.0, 1.0)) * 0.2, 0.21, 0.01);
    mouth -= Circle(uv, vec2(0.0, 0.1), 0.2, 0.01);

    mask -= mouth;
    col = vec3(mouth);
    float ry = clamp(abs(sin(iTime)), 0.0, 1.0);


    col = vec3(1.0, 1.0 - ry, 0.0) * mask;
    fragColor = vec4(col, 1.0);
}
