float map(vec3 p)
{
    vec3 q = mod(p,0.8) *  2.0 - 1.0;
    return length(q) - (sin(iTime)+1.2)*0.15; //pulse action
}

float trace(vec3 o, vec3 r)
{
    float t = 0.0;
    for(int i = 0; i < 512; i++)
    {
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.2;
    }
    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x/iResolution.y;

    vec3 r = normalize(vec3(uv, 2.0));

    vec3 o = vec3(0.0,0.0,iTime*1.0); // camera

    float t = trace(o, r);

    float fog = 1.0/ (1.0 + t * t * 0.1);

    vec3 fc = vec3(fog);

    // Output to screen
    fragColor = vec4(fc,0.0);
}
