void mainImage(out vec4 O, vec2 C) {
	O=vec4(.1,.4,.7,1);
    for(float g,e,i;i++<70.;){
        vec3 p=g*vec3(C/iResolution.y-.5,1.)+vec3(-.5,1,iTime);
        p.xy=abs(p.xy)-1.4;
        p.xy=vec2(atan(p.x,p.y)+iTime,cos(length(p.xy)));
        g+=e=.3*length(vec2(p.y-sin(p.x*30.)*.02,fract(p.z*2.)-.5))-.01;
        e<.01?O+=.8/i:O;
	}
}
