// variant of https://shadertoy.com/view/tdlfWX
// variant of https://shadertoy.com/view/XsdBW8

void mainImage(out vec4 O, vec2 U) {

    float T = iTime, t=1.,a;
    mat2  R = mat2( cos(-.3*T +vec4(0,33,11,0)) );
    vec3  q = iResolution, A,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),        // ray direction
          p = 30./q;                              // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p.xzy - vec3(0,0,T),
        q.xy *= R,
        A = abs(q), t = abs(max(A.x,A.y)-6.)-2.,  // bounding hollow box
        a = atan(q.y,q.x),
        t = max(t, sin(a-floor(q.z*4.)/4.*1.047)),// helix: stairs side
        t = max(t, sin(a-q.z*1.047+2.3) ),        // helix: roof side
        p += .7*t*D;                              // step forward = dist to obj
}
