/*
Ryan Enslow
March 15, 2020
Julia and Mandelbrot set relationship visualization
*/


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv = 3.*(uv-vec2(0.5));
    uv.x -= 1.25;
    vec3 col;
    vec3 col2;
    vec2 z = uv;
    vec2 c = z;
    vec2 nz;
    int iter = 70;

    int i;
    for (i=0;i<iter;i++)
    {
        nz.x = z.x*z.x - z.y*z.y + c.x;
        nz.y = 2.0*z.x*z.y + c.y;    
        if (length(nz) > 2.0)
        {
            col = vec3(float(i)/10.);
            break;
        }
        else
        {
            col = vec3(1.);
        }
        z = nz;
    }

    //Julia set
    vec2 xy = iMouse.xy/iResolution.y; xy = 3.*(xy-vec2(0.5));xy.x -= 1.25;

    vec2 cj = xy; vec2 j = uv; vec2 nj;
    col2 = vec3(.2,.45,.7);

    for (i=0;i<iter;i++)
    {
        nj.x = j.x*j.x - j.y*j.y + cj.x;
        nj.y = 2.0*j.x*j.y + cj.y ; 
        if (length(nj) > 2.0)
        {
            float e = float(i);

            //col2 = vec3(cos(e+1.),cos(e+0.),cos(e+2.));
            col2 = e*vec3(0.8)/15.;
            break;
        }
        else
        {
            col2 = vec3(0.1);
        }
        j = nj;
    }

    // Output to screen
    fragColor = vec4(mix(col,col2,0.5),1.0);
}
