#define PI 3.14159265359
#define PI0_5 1.57079632679
#define PI1_5 4.71238898038
#define PI2 6.28318530718

float psin(float angle){
    return (sin(angle)+1.0)/2.0;
}

vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

vec3 step_color(float step) {
    float r = psin((1.0 + step) * 200.0);
    float g = psin((1.0 + step) * 300.0);
    float b = psin((1.0 + step) * 500.0);
    return  vec3(r, g, b);
}

vec4 background_layer(vec2 fragCoord, float t, float step) {
    float last_step = step-1.0;
	return vec4(step_color(last_step), 1.0);
}

vec4 circle_layer(vec2 fragCoord, float t, float step) {
    vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * 0.5;
	float radius = 0.25;
    float wobble_factor = 0.1;

	return circle(uv, center, (radius + wobble_factor * sin(t)) * iResolution.y, step_color(step));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float t = iTime * 5.0;    
    float step = 1.0+mod(floor((PI1_5+t)/PI2),1000000.0);

	vec4 layer1 = background_layer(fragCoord, t, step);
	vec4 layer2 = circle_layer(fragCoord, t, step);

	fragColor = mix(layer1, layer2, layer2.a);
}
