void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float time = clamp(mod(iTime, 3.5) - 0.25, 0.0, 3.0);
    vec2 uv = fragCoord / 64.0;

    uv.x -= min(1.0, time / 1.5 * (1.0 - mod(floor(uv.y), 2.0)));
    uv.y -= max(0.0, (time - 1.5) / 1.5 * (1.0 - mod(floor(uv.y), 2.0)));

    fragColor = vec4(0.5);
    if (fragCoord.x < 512.0 && fragCoord.y < 512.0) {
        fragColor = vec4(mod(floor(uv.x) + floor(uv.y), 2.0));
    }
}
