float xscale = 0.50;
float yscale = 12.0;
float speed  = 0.08;
float xdistort = 0.05;
float ydistort = 0.01;

void mainImage(out vec4 fragColor,in vec2 fragCoord)
	{
    // sample
    vec2 uv = fragCoord/iResolution.xy;

    // output
    if (iMouse.x < fragCoord.x)
    	{
        vec2 uv_warp = vec2(uv.x/xscale,uv.y/yscale + iTime*speed);
    	vec2 uv_samp = vec2(texture(iChannel1,uv_warp).r,texture(iChannel1,vec2(uv_warp.x,uv_warp.y + speed)).r);
    	fragColor = texture(iChannel0,vec2(uv.x+(uv_samp.x-uv_samp.y)*xdistort,uv.y + uv_samp.y*ydistort));
        }
    else
        fragColor = texture(iChannel0,uv);
	}
