#define PI 3.14159
#define TAU PI*2.
#define t iTime

mat2 rz2 (float a) { float c=cos(a), s=sin(a); return mat2(c,s,-s,c); }
float cyl (vec2 p, float r) { return length(p)-r; }
float cube (vec3 p, vec3 r) { return length(max(abs(p)-r,0.)); }

vec2 path(float z){
    float x = sin(z) - 1.0 * cos(z * 1.) - 4. * sin(z * 0.12345);
    float y = cos(z) - 1. * sin(z * 1.) - 1. * cos(z * 2.12345);
    return vec2(x,y);
}

vec2 path2(float z){
    float x = sin(z) + 1.0 * cos(z * 1.) + .2 * sin(z * 0.12345);
    float y = cos(z) + 1. * sin(z * 1.) + .5 * cos(z * 2.12345);
    return vec2(x,y);
}

vec2 modA (vec2 p, float count) {
    float an = TAU/count;
    float a = atan(p.y,p.x)+an*.5;
    a = mod(a, an)-an*.5;
    return vec2(cos(a),sin(a))*length(p);
}
float smin (float a, float b, float r)
{
    float h = clamp(.5+.5*(b-a)/r,0.,1.);
    return mix(b, a, h) - r*h*(1.-h);
}

float GetDist (vec3 p)
{

    vec3 q =p;    
    vec2 o = path(p.z) /5.0;
    p = vec3(p.x,p.y,p.z)-vec3(o.x,o.y,0.);
    vec2   o2 = path2(p.z) / 5.0;
    q = vec3(q.x,q.y,q.z)-vec3(o2.x,o2.y,0.);
    p.xy *= rz2(p.z*0.3)*0.7;
    q.xy *= rz2(q.z*0.1)*.5;
    p.zy = mod(p.zy ,5.) - 2.5;
    q.zy = mod(q.zy ,5.) - 2.5;
    q.zx = mod(q.zx ,5.) - 2.5;   
    p.xy *= rz2(p.z*sin(t*.3+250.)*2.); 
    q.xy *= rz2(q.z*sin(-t*0.02+250.));
    float cyl2wave = .3+1.5*(sin(p.z+t*0.5)*.1);
    float cylfade = 2.-smoothstep(.0,8.,abs(p.z));
    float cyl2r = 0.02*cyl2wave*cylfade;
    float cylT = 1.;
    float cylC = 1.;
    vec2 cyl2p = modA(p.xy, 8.)-vec2(cyl2wave, 0)*cylfade;
    float cyl2 = cyl(cyl2p, cyl2r);
    cyl2p = modA(p.xy*rz2(-p.z*cylT), cylC)-vec2(cyl2wave, 0)*cylfade;
    vec3 cubP = p;
    float cubC = 0.1;
    cubP.z = mod(cubP.z, cubC)-cubC*1.;
    cubP.xy *= rz2(t*3.);
    float cyl2a = smin(cyl2, cube(cubP,vec3(.1*cyl2wave*cylfade)),.5);
    cyl2wave = .3+1.5*(sin(q.z+t*0.5)*.2);
    cylfade = 2.-smoothstep(.0,8.,abs(q.z));
    cyl2r = 0.06*cyl2wave*cylfade;
    cylT = 1.;
    cylC = 1.;
    cyl2p = modA(q.xy, (abs(sin(t*0.1)+3.)))-vec2(cyl2wave, 0)*cylfade;
    cyl2 = cyl(cyl2p, cyl2r);
    cyl2p = modA(q.xy*rz2(-q.z*cylT), cylC)-vec2(cyl2wave, 0)*cylfade;
    cubP = q;
    cubC = 0.1;
    cubP.z = mod(cubP.z, cubC)-cubC*1.;
    cubP.xy *= rz2(t*3.);  
    float cyl22 = smin(cyl2, cube(cubP,vec3(.1*cyl2wave*cylfade)),.5);
	return smin(cyl2a,cyl22,0.1)*.6;
}

float marchCount;

float RayMarch (vec3 ro, vec3 rd) 
{
 float dO = 0.;        
 marchCount = 0.0;
 for (int i=0; i<68; i++) 
  {
   vec3 p = ro + dO * rd;
   float dS = GetDist (p);
   dO += dS;
   if (dS<0.001 || abs(dO)>100.) break;
   marchCount+= 2./dS*.0009;
  }
 return dO;
}

vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(0.1,0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
    	GetDist(p-e.yyx));

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy) / iResolution.y;
    vec3 col = vec3(0);    
    vec3 ro = vec3(0.,0.,-t*2.);
    vec3 rd = normalize(vec3(uv.x,uv.y,.8));
    float the = iTime *0.1;
    rd.yx *= mat2(cos(the), -sin(the), sin(the), cos(the));
    float d = RayMarch (ro,rd);
    vec3 p = ro + rd *d;
	float fog = 1. / (2. + d * d *.1);
    vec3 fc = vec3(fog);
   fc *= marchCount * vec3(1., (sin(t +-p.z)*.5),(tan(t +-p.z)*.5));
    fc *= vec3(fog);
    vec3 sky = vec3(0., .1, 0.1);
    fc = mix(sky, fc, 1.5/(d*d/1./1.*.01));
    // Output to screen
    fragColor = vec4(fc,1.0);
}
