void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 wood = texture(iChannel0, uv).rgb;
    vec3 london = texture(iChannel1, uv).rgb;
    float cut = smoothstep(1., 0., length(london));
    vec3 col = mix(wood, wood*.5, cut);
    fragColor = vec4(col,1.0);
}
