const float PI = 03.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    //st *=  1.-length(1.0*st)+sin(iTime+st.y+st.x) ;
	st *= PI*2.;
    float brightness = 0.0;
	vec3 baseColor = vec3(0.2, 0.6, 0.8);
	float speed = iTime * 0.5;

	brightness += st.y*0.01 / (sin(PI * st.x) * sin(PI * speed + floor(st.x)));
    brightness += st.y*0.01 / (sin(PI * st.x*0.01) * sin(PI * speed + floor(st.x)));

    //Other options to play around with -- Just uncomment 

	//brightness += st.y*0.01 / (sin(PI * st.x*0.01) * sin(PI * speed + floor(st.x*-0.05)));
	//brightness += 0.02 / abs(sin(PI * st.y) * sin(PI * st.x) * sin(PI * speed + floor(st.x)));


    fragColor = vec4(baseColor * brightness, 1.0);
}
