#define S smoothstep

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-0.5*iResolution.xy) / iResolution.y;
    uv.y += 0.04*sin(iTime*3.2 + uv.x*28.);  
    uv.y += 0.032*sin(iTime*1.4 + uv.x*32.);
    uv.y += 0.022*sin(iTime*2.2 + uv.x*10.);
    uv.y += 0.012*sin(iTime*2.0 + uv.x*12.);
    uv.y += 0.015*sin(iTime*.2 + uv.x*41.3);

    vec3 col = mix(vec3(0.), vec3(1., 1., 1.), S(0.12, .02, abs(uv.y)));

    col *= 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col += mix(vec3(0.), vec3(1., 1., 1.), S(0.01, .002, abs(uv.y)));

    fragColor = vec4(col,1.0);
}
