#define S(a,b,t) smoothstep(a,b,t)
float N11(float p) {
    float a = fract(p*345.65);
	a += dot(a, a+34.45);
    return fract(a);
}

vec3 wave(vec2 uv, float t, vec3 col, float seed, float h) {
    uv += .22;
    float ti = t*4.5;
    vec3 m = vec3(0.);
    h = (h*0.1)+.22;
    float x = uv.x;
    float y = uv.y;
    float w1 = sin(x+seed*ti);
    float w2 = sin(x*2.+seed*(ti*1.5+seed));
    float w3 = sin(x*3.+seed*ti);
    float wavey = (w1+w2+w3)*.15;
    //wavey += sin(t*3.+uv.x*seed*6.)*.3;
    uv.y += wavey*.1;
    m = smoothstep(h*.99,h,uv.y)*col;
    m = mix(m, smoothstep(h,h*0.90,uv.y)*col,uv.y)*0.8;
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    float  t = iTime*.6+m.x+(m.y*4.);

	vec3 col = vec3(0.);

    col += wave(uv,t,vec3(.1,.5,.6),N11(12.),1.);
    col += wave(uv,t,vec3(.1,.5,.55),N11(1.),2.3);
    col += wave(uv,t,vec3(.1,.5,.5),N11(34.),3.);
    col += wave(uv,t,vec3(.1,.5,.45),N11(21.),4.);

    fragColor = vec4(col,1.);
}
