void mainImage( out vec4 C, in vec2 U )
{
    vec2 uv = U/R;
    vec2 uvn = (U - R*0.5)/R.y;

    C -= C;
    C = mix(C,vec4(0.,0.01,0.1,1.) - sin(uvn.xyyy*1. - 0.5)*0.1,T3(U).x*1.);

    C = mix(C,vec4(0.,0.2,0.1,1.) + abs(sin(uvn.yxxy*1. + iTime)*0.9), T2(U).x);
    vec4 me = T1(U);
    C = pow(C,vec4(0.4545));
}

vec4 r42(vec2 u){
    return texture(iChannel1,u/iResolution.xy);
}

const float velScale = 0.001;

int idxNeighbors = 0;
vec4[] neighbors = vec4[15](vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9),vec4(9));

vec4 get(vec2 u){
    vec4 g = T(u);
    vec2 muv = (iMouse.xy)/iResolution.xy;
    g.zw = mix(g.zw, -normalize(muv - g.xy ),0.4*float(iMouse.z > 0.)*smoothstep(1.,0.,length((g.xy - muv)*R.y/R.x)*6. ));


    #define pump(t,a) (floor(t) + pow(fract(t),a))
    g.zw = mix(g.zw,vec2(0,-1.)*rot(0.5*pi*pump(iTime*0.1,20.)),0.007);




    g.xy += vec2(g.z*R.y/R.x,g.w)*velScale;

    if (abs(g.x - 0.5)>0.5){
        g.z *= -1.;
        g.x += 2.*g.z*velScale;
    }
    if (abs(g.y - 0.5)>0.5){
        g.w *= -1.;
        g.y += 2.*g.w*velScale;
    }


    if (T3n(g.xy).x > 0.){
        vec2 normalRange = 4./R;
        vec2 n = normalize(vec2(
            T3n(g.xy + vec2(normalRange.x,0.)).x - T3n(g.xy + vec2(-normalRange.x,0.)).x,
            T3n(g.xy + vec2(0.,normalRange.y)).x - T3n(g.xy + vec2(0.,-normalRange.y)).x
            ));
        vec2 t = n*rot(0.5*pi);

        //g.zw -= g.zw*2.;
        //g.zw *= rot(0.5*pi);


        g.zw = reflect(g.zw,n);
        //g.xy += g.zw*2.*velScale;
        g.xy -= n*0.003;


        //g.zw -= 2.*n*g.zw*float(dot(g.zw,t) > 0.);
    }

    return g;
}

