#define check(dir,times) \
    for(float i = 1.; i < times + 1.; i++){             \
        vec4 other = get(U + dir*i*stepSz);             \
        if(other != me){                                \
            if(length(uv - other.xy) < length(uv-me.xy))\
                me = other; meuv = U + dir*i*stepSz;            \
        }\
    }
#define checkNeighbors(dir,times) \
    for(float i = 1.; i < times + 1.; i++){             \
        vec4 other = get(me.xy*R + dir*i*stepSz);                    \
        if(other != me){                                \
            neighbors[idxNeighbors++] = other;                             \
        }\
    }


void mainImage( out vec4 C, in vec2 U )
{
    vec2 uv = (U)/R;
    vec2 uvn = (U - R*0.5)/R.y;




    C = T(U);

    vec4 me = get(U);

    vec2 meuv = U;

    float steps = 15.;
    float stepSz = 1.;
    check(vec2(0,1)    ,steps);
    check(vec2(0,-1)   ,steps);
    check(vec2(1,0)    ,steps);
    check(vec2(-1,0)   ,steps);
    check(vec2(1,1)    ,steps);
    check(vec2(-1,-1)  ,steps);
    check(vec2(1,-1)   ,steps);
    check(vec2(-1,1)   ,steps);


    steps = 10.; stepSz = 3.;
    checkNeighbors(vec2(0,1)    ,steps);
    checkNeighbors(vec2(0,-1)   ,steps);
    checkNeighbors(vec2(1,0)    ,steps);
    checkNeighbors(vec2(-1,0)   ,steps);
    checkNeighbors(vec2(1,1)    ,steps);
    checkNeighbors(vec2(-1,-1)  ,steps);
    checkNeighbors(vec2(1,-1)   ,steps);
    checkNeighbors(vec2(-1,1)   ,steps);

    for (int i = 0; i < idxNeighbors; i++){
        vec2 dir = neighbors[i].xy - me.xy; 
        //g.zw += normalize(dir)*smoothstep(1.,0.,exp(-dot(dir,dir)));

        float ldir = length(dir);
        if(ldir > 0.)
            me.zw -= normalize(dir)*smoothstep(1.,0.,ldir*100.)*0.07;
        //g.y += 0.0001;
    }

    C = me;


    if(iFrame < 2){


        C = r42(U*11.);
        if(T3n(C.xy).x == 0. && sin(C.z*2414.124) >0.0){


            C.zw = C.zw*2. - 1.;
            C.zw *= 0.1;
        } else {
            C = vec4(-1000);
        }
        //C = T3(U).xxxx;
        //C = vec4(sin(U.x/10.),0.0,1.0,1.0);
    }
}

