#define val(U, X,Y)  step(  s(U,Y) ,  texelFetch( iChannel0, ivec2(U+vec2(X,Y) ) % 1024, 0 ).x  )

// threshold.  left: constant.  right: slight density gradient
#define s(U,Y)         ( 0.3)

void mainImage( out vec4 O, vec2 u )
{
    float z = 10.;
    vec2 U = u/z ,
         I = round(U), F = U-I, S = step(0.,F); 
    F = abs(F); I--;


    O = vec4(val(U, 0.,0.) );         
    vec4 V = vec4( val(I, 1.-S.x,S.y), val(I, S.x,S.y), 
                   val(I, S.x,1.-S.y), val(I, 1.-S.x,1.-S.y));

    if (      V.x == V.z && V.x != V.y 
         && ( V.y>0.     || V.y != V.w ) )
        O = mix( O, 1.-O, smoothstep(-1./16.,1./16.,.5-(F.x+F.y)) );

    O = clamp(O,0.,1.);
}
