#define PI   3.1415926535
#define TAU  (2.0*PI)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = (fragCoord - iResolution.xy/2.0) / iResolution.y * 2.0;

    float theta = atan(uv.y,uv.x); // from -pi to pi
    float dist = distance(uv, vec2(0.0)); // distance from center, 1 is the top edge

    vec3 col = vec3(1.0);
    if (mod(theta - pow(dist + 1.0, 1.7)*11.0 + iTime*4.0, TAU) < 0.3)
        col *= 0.0;

    // Output to screen
    fragColor = vec4(col,1.0);
}
