void mainImage( out vec4 O, in vec2 u )
{

    vec2 R = iResolution.xy,
        p = 20.*(u - .5*R ) / R.y, q, I,P;

    float s = 2., lim = 2., i=-1., N = 2.*floor(lim)+1.;

    q =  p - s*clamp(round(p/s),-lim,lim); // opRepLim

    I = round(p/s); 
    if ( I == clamp(I,-lim,lim) ) {        // index in grid
        I += lim;
        i = I.x + (N-1.-I.y)*N;
    }

    O.rg =  .1+.1*sin(q);                  // show grid
    O.b = i/(N*N);                         // blue = index on [0,1]

    i = floor(mod(iTime*3.,N*N));
    P = vec2(mod(i,N), N-1.-floor(i/N));
    P = (P-lim)*s;                         // uv index to screen
    O += smoothstep(30./R.y,0., length(p-P)-.3);  // place disk there

    O = sqrt(O);                           // to sRGB
}
