void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec4 effect = vec4((uv.x+uv.y)*abs(sin(iTime)),0,0,0.5);
    //vec4 effect = vec4(0);

    vec2 distcord =  vec2( uv.x, uv.y / abs(sin((uv.x+iTime)*2.))); //vec2( uv.x, uv.y/0.1);

    vec4 tex = texture(iChannel0,distcord);
    if(tex.y > 0.3 && tex.x < 0.25 && tex.z < 0.25)
        tex = vec4(0);

    vec4 col = tex + effect;
    // Output to screen
    fragColor = col;
}
