vec2 center = vec2(0.);
float radius = .5;

vec2 closestCenter(vec2 c, vec2 uv){
    vec2 newC = c;

    for(float i=-1.; i<=1.; i++){
        for(float k=-1.; k<=1.; k++){

            vec2 tempC = vec2(c.x+i,c.y+k);

            if(distance(newC,uv) > distance(tempC,uv))
                newC = tempC;
        }   
    }

    return newC;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5/iResolution.xy)/iResolution.y;

    float t = iTime*.3;

    uv+=vec2(sin(t),cos(t));

    uv *= 3.;

    vec3 col = vec3(0.);

    uv = fract(uv);

    center += vec2(sin(t),cos(t));

    center = fract(center);

    center = closestCenter(center,uv);

    float test = smoothstep(radius, radius + sin(iTime)*.05+.05,distance(uv,center));

	vec3 color = vec3(texture(iChannel0,uv).r);
	vec3 color1 = vec3(texture(iChannel1,uv).r);



    col.rg = vec2(uv.x,uv.y);

    col = (color * (1.-test)) + (color1*test);


    fragColor = vec4(col,1.0);
}
