float N11(float p)
{
    vec2 v = vec2(fract(743.83*p),-34.487+p);
    return fract(453.281347*dot(v,v));
}

float func(vec2 p,float seed,float offset)
{
    float horizOff = mix(offset,0.,p.y);
    float m1 = horizOff+mix(2.,.5,p.y)*sin(mix(.5,5.,p.y)*p.y-iTime*15.*(N11(seed)+.5))*.1+.5;
    float m2 = +horizOff+(N11(seed*2.)+.5)*mix(2.,.5,p.y)*cos(mix(.5,5.,p.y)*p.y-iTime*15.*(N11(seed*3.)+.5))*.1+.5;
    float m =10.*(p.x-m1)*(m2-p.x)/abs(m2-m1);
    return smoothstep(0.,1.,m) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Time varying pixel color
    float m = 0.;
    for(float i = 1.; i<10.; i++){
        m+=func(uv,N11(i),.2/(i-5.));
    }
    vec2 p = vec2(.5,0.);
    float dist = (cos(iTime*.0876)+.4*sin(iTime*3.178+3.3827)+.17*sin(iTime*5.92)+3.)/3.*length(uv-p); 
    float light = 2.*(1.-dist);
    vec3 col = vec3(light*.2+m,light*.03+m*m,0.);
    // Output to screen
    fragColor = vec4(col,1.0);
}
