#define r(a) mat2(cos(vec4(0, 11, 33, 0) + a))
void mainImage(out vec4 o, in vec2 u) {
	vec2 R = iResolution.xy;
    vec3 p;
	float t = .0;
    for (int i = 0; i < 32; i++) {
        p = mod(p, 4.) - 3.14;
        p = vec3((u + u - R) / R.y, 1) * (t += .5 * min(min(length(p.xz) - .5, length(p.xy) - .5), length(p.yz) - .5));
		p.xy *= r((p.z += iTime * 3.) * .3);  

    }
    o += 1. / t;
}


