void mainImage( out vec4 Q, in vec2 U )
{
    Q = (cos(atan(A(U).x*50.)*4.+vec4(1,2,3,4)));
    vec4 
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec3 no = normalize(vec3(e.x-w.x,n.x-s.x,.001));
    Q *= 0.5+0.5*texture(iChannel1,no);
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    vec4 m = 0.25*(A(U+vec2(0,1))+A(U-vec2(0,1))+A(U+vec2(1,0))+A(U-vec2(1,0)));
    ///*
    	// wave : 
   	 	Q.y += dt*(m.x-Q.x);
    	Q.x += dt*Q.y;
    //*/
    /*
		// diffusion :
			Q.x += dt*(m.x-Q.x);
	*/

    if (length(U-vec2(.4,.3)*R)<10.) Q.x = 0.;
    if (length(U-vec2(.6,.7)*R)<15.) Q.x = 0.;
    if (length(U-vec2(.8,.2)*R)<30.) Q.x = 0.;
    if (iFrame < 1) {
    	Q = vec4(exp(-.25*length(U-0.5*R)),0,0,0);
    }
}

