// modern re-golfing of https://shadertoy.com/view/ldGSDG

// 205 chars in B&W, 230 chars in colors , +4 because of Angle fails on max(W(U),W(U+v))

#define W                                                       \
    min(1., 2. - 9.* abs( .7                                    \
                          -( ( l = length( V = vec2(1,1.9) - mod( U+=vec2(0,7), vec2(2,2.8) ) ) )\
                             > V.y/.7                           \
                               ?  l                             \
                               :  length( abs(V) - 1. ))))      \
     * ( .5 + .4* abs(.6-V.y) )                  // depth: face A
 //  * ( 1. - .3* abs(.6-V.y) )                  // depth: face B 
void mainImage(out vec4 O, vec2 u) {
    vec2 U = u / iResolution.y / .1, V;
    float l, w = W;
    O += max( w, W );
    O.x == w ? O.bg*=.8 : O.rg *= .8;
}// color: + 25 chars
