#define A(x) smoothstep(3., 0., (x)*R.y)
//used often as alias
#define oz vec3(-1,0,1)
//return rotation matrix, inout radians
#define r2(r) mat2(sin(r-oz.xyyz*1.585))

#define PI 3.14
#define TAU 6.28

void mainImage( out vec4 O, in vec2 u )
{
    vec2 R = iResolution.xy, p = (u+u-R)/R.y, z = u/R;

    float w = sin((p.x+.6*p.y - .5*iTime)*1.85*PI);
    float v = min(25. * z.x * (1.-z.x) * z.y * (1.-z.y), 1.);

    p *= 1. + (.03-.03*w); // Fake parallax from waving

    O.rgb = mix(vec3(1), vec3(0, 98, 51)/255., A(p.x));

    float t = max(length(p)-.5, -(length(p-vec2(.14,0))-.4) );

    p.x -= .215;

    float l = length(p);
    float a = atan(p.y,p.x)/TAU + .5;
    a = ((fract(a * 5.)*2.-1.) / 5.)*.5*TAU;

    p = vec2(cos(a), sin(a)) * l;
    t = min(t, max(p.x - .25, -( r2(radians(-18.)) * vec2(p.x - .25, abs(p.y)) ).y  ));

    O.rgb = mix(O.rgb, vec3(210, 16, 52)/255., A(t));

    O.rgb += w*.2; // Fake shading for waving
    O.rgb *= 1.-.7*exp(-2.*v);
}
