const float PI = 3.14159265359;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float resMin = min(iResolution.x, iResolution.y);
    float resMax = max(iResolution.x, iResolution.y);
    vec2 uvMin = fragCoord/resMin;
    vec2 uvMax = fragCoord/resMax;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   	uvMin.y -= 0.5;

    uvMin.y *= sin(uv.x*PI*2.+iTime*3.)+1.*10.;

    uvMin.y = uvMin.x+sin(sin(uvMin.y*4.));

    uvMin.x -= abs(sin(uv.y*PI));
    uvMin.x -= iTime*3.;
    //uv.x = uv.x*sin(iTime)+1.;//-iTime;
    //uv.y = uv.y*cos(iTime)+1.;//-iTime;
   // uvMin.x /= cos(fract(uvMin.y)+iTime);
    //uv.xy *= cos(iTime/8.)*(uv.x+uv.y)*uv.y*5./uv.x;

	float uvPlus = uvMin.x+uvMin.y;
    float uvSub = uvMin.x-uvMin.y;
    // Time varying pixel color
    float plus = cos(uvPlus);//*cos(uvPlus*PI);
    float sub = cos(uvSub);//*cos(uvSub*PI);
    float flame = sqrt(pow(plus,25.) * pow(sub,25.));
    vec3 col = vec3(flame*uv.x*2.5, flame*flame*uv.x*2., flame*flame*uv.x*uv.y*3.5);



    // Output to screen
    fragColor = vec4(col,1.0);
}
