vec2 N(float angle)
{
    return vec2(sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 mouse = iMouse.xy/iResolution.xy*(iTime/27.);

    uv *= 1.25;
    vec3 col = vec3(0);

    uv.x = abs(uv.x);
    uv.y += tan((5./6.)*3.1415)*.5;
    vec2 n = N((5./6.) * 3.1415);
    float d = dot(uv-vec2(.5, 0.), n);

    uv -= n*max(0., d)*2.;

   // col += smoothstep(.01, .0, abs(d)); // guide line

    n = N(mouse.y*(2./3.)*3.1415);
    float scale = 1.;
    uv.x += .5;
    for(int i=0;i<5;++i)
    {
    	uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        uv.x = abs(uv.x);
        uv.x -= .5;
        uv -= n * min(0., dot(uv, n)) * 2.;
    }

    d = length(uv - vec2(clamp(uv.x, -1., 1.), 0.));
    col += smoothstep(1./iResolution.y, .0, d/scale);
    float angle = iTime*0.0;
    float s = sin(angle);
    float c = cos(angle);

    mat2 rotationMatrix = mat2( c, s,
                               	-s,  c);

    vec2 pivot = vec2( 0., 0.);
    uv = rotationMatrix * (uv - pivot) + pivot;

    uv /= scale;
    col.r += uv.r*sin(iTime/2.)/2.;
    col.b += uv.r*sin(iTime/3.)/2.;
    col += texture(iChannel0, uv*2.+iTime/35.).rgb;
    col += texture(iChannel1, uv+iTime/30.).rgb/2.2*(1.+(sin(iTime/4.)+1./2.));

    col -= 0.1;
    fragColor = vec4(col,1.0);
}
