//hash function taken from https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
float smoothNoise(vec2 uv)
{
    vec2 lv = fract(uv);
    vec2 id = floor(uv);

    lv = lv * lv * (3. - 2.*lv);

    float bl = hash12(id);
    float br = hash12(id + vec2(1., 0.));

    float b = mix(bl, br, lv.x);


    float tl = hash12(id + vec2(0., 1.));
    float tr = hash12(id + vec2(1., 1.));

    float t = mix(tl, tr, lv.x);

    float c = mix(b,t, lv.y);
    return c;
}

float perlin(vec2 uv)
{

    float c = smoothNoise(uv * 4.* sin(iTime * 0.01 + .113));
    c += smoothNoise(uv * 8. * sin(iTime * 0.12)) * 0.5;
    c += smoothNoise(uv * 16. * sin(iTime * 0.1 + 1.213)) * 0.25;
    c += smoothNoise(uv * 32.* sin(iTime * 0.042 + .213)) * 0.125;
    c += smoothNoise(uv * 64. * sin(iTime * 0.0037 + .113)) * 0.0625;
    c /= 2.;
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 2.;
    uv += vec2(10., -9.);

    float c = perlin(uv * 0.1);

    vec3 color1 = vec3(0.4, 0.8, 0.5);
    vec3 color2 = vec3(0.2, 0.1, 0.2);
    vec3 color3 = vec3(0.5, 0.5, 0.95);

    // Time varying pixel color
    vec3 col = vec3(mix( mix(color1, color2, c * 1.2) * 0.5, color3, fract(c * 12.321) ));


    // Output to screen
    fragColor = vec4(col,1.0);
}
