float dimx = 32.0;
float dimy = 32.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // vec2 til = iResolution.xy / vec2(dimx,dimy);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= vec2(dimx,dimy);

    uv = floor(uv);

    uv /= vec2(dimx,dimy);

    vec4 col = texture(iChannel0, uv);

    // Output to screen
    fragColor = col;
}
