vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv = -1.0 + 2.0 * uv;

    float adjustAngle = radians(0.0);
    uv = rotate(uv, adjustAngle);

    // tint it blue
    vec3 col = vec3(0.0, 0.0, 0.0);

    // the cross
    col.rgb += abs(uv.x + uv.y) < (0.12 * aspect) ? 1.0 : 0.0;
    col.rgb += abs(uv.y - uv.x) < (0.12 * aspect) ? 1.0 : 0.0;
 	col.b = 1.0;

    col.r += 0.1;
    col.g += 0.45;
    fragColor = vec4(col,1.0);
}
