float v = -0.6;
float u = 0.8;
float zoom = 0.5;

///////////////////////////////////////////////////////

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//zoom = iMouse.y/45.;
	//u = iMouse.x/800.;


	float zoomFactor = 1.0 / zoom;
	float ushift = (1.0 - zoom) * (u - 0.5) * zoomFactor;
	float vshift = (1.0 - zoom) * (v - 0.5) * zoomFactor;

	u = 1.- u - ushift;
	v = 1.- v - vshift;

	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.x = (uv.x - u + zoomFactor * 0.5) * zoom;
	uv.y = (uv.y - v + zoomFactor * 0.5) * zoom;

	fragColor = texture(iChannel0, uv);
}
