#define SCALE 3.
#define TIMESCALE 1.

#define ORANGE vec3(.9,.5,0.)
#define WHITE_BLUE vec3(0.,.4,.9)
#define PURPLE vec3(0.76,0.24,0.89)
#define BLUE vec3(0.,0.,.8)
#define BLACK vec3(0)
#define WHITE vec3(.98)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = SCALE * (2.0 * fragCoord - iResolution.xy)/iResolution.y;
    vec2 center = vec2(0);
    float g = -2.;
    center.x+=sin(uv.y*g+iTime*TIMESCALE);
    center.y+=cos(uv.x*g+iTime*TIMESCALE);
    float d = distance(uv ,center);
	float k = abs(sin(d*6.14 - iTime*TIMESCALE));
    k *= tanh(d/k)/tanh(k)/center.x/center.y;
    vec3 col = mix(vec3(.83,.5,.4), WHITE, k);
    fragColor = vec4(col, 1.);
}
