void drawStars(inout vec3 col,vec2 uv,vec2 c){
   uv-=c;

   uv*=2.8;

   float l =length(uv);
   float arc = atan(uv.y,uv.x); 

   float d = abs(dot(vec2(0,1),uv));
   float d1 = abs(dot(vec2(1,0),uv));
   float ctv = c.x*10.0;

   d+=pow(l,1.1+0.6*cos(iTime*2.0+ctv));
   d1+=pow(l,1.1+0.6*cos(iTime*2.0+ctv));

   float v =0.0005/l;  

   v+=0.01/(d);
   v+=0.01/(d1);
   v=pow(v,1.6);

   col+=vec3(cos(iTime+ctv)*0.1+0.5,sin(iTime+ctv)*0.4+0.5,sin(iTime)*0.3+0.7)*v;
}


void drawStarGroup(inout vec3 col,vec2 uv,float starNum){
    for (float i=0.0;i<20.0;i++){
        drawStars(col,uv,vec2(i*0.1*sin(i),cos(i)*0.3));
    }
}

vec3 starsColor(highp vec3 position) {

     vec2 u = position.xz*5.0;
     vec3 stars = vec3(0.0);
     drawStarGroup(stars,u,6.0); 

return stars;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 stars = starsColor(iResolution);

    vec3 col = vec3(0.5,0.09,1.6);

    // Output to 
    fragColor.rgb += stars;
    fragColor = vec4(col,1.0);
}
