void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R,
         M = iMouse.xy/R;

    u /= exp2(floor(5.*M.x));  // wavelength
                      // random BW pattern at wavelength f(M.x)
    float v = step(.5, texelFetch(iChannel0,ivec2(u + iTime*777.)%1024,0).r);

    if (U.x > .5) v *= .1*M.y; // right: apply contrast

    O = vec4( pow(v, 1./2.2)); // to SRGB
}
