vec2 Center = vec2(0.5,0.5);

float StepLength = 0.53;

float Speed = -3.0;

float RoundSize = 30.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // One friend said: "If you want them round, uv = frag/res.y, not xy."
   // This is my personal habit.
   vec2 uv = fragCoord/iResolution.xy;

   float dis = distance(uv, Center);

   vec2 dir = normalize(uv - Center);

   dis = cos(RoundSize*dis+iTime*Speed) * StepLength;

   vec2 normalValue = dir*dis;

   normalValue = normalValue * 0.5 + 0.5;

   fragColor = vec4(normalValue, 1.0, 1.0);
}
