float pattern_circle(vec2 uv,vec2 pos, float r ,float blur)
{
	uv -=pos;
    float d = length(uv);
    float c;
    //1-smooth(a,b) = smooth(b,a)
    c =smoothstep(r+blur,r,d);
    return (c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //uv range <(-0.5,-0.5 * scale) ~ (0.5,0.5 * scale)>
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.x;


    //smoothstep()
    float face = pattern_circle( uv,vec2(0.,0.),0.2,0.02);
    float eye1 = pattern_circle( uv,vec2(-0.1,0.1),0.04,0.01);
    float eye2 = pattern_circle( uv,vec2(0.1,0.1),0.04,0.01);
    float mouth = pattern_circle( uv,vec2(0.,0.),0.16,0.02)-pattern_circle( uv,vec2(0.,0.07),0.18,0.02);
    float mask = face- eye1 - eye2 - mouth;
    vec3 col = vec3(1,1,0);

    // Output to screen
    fragColor = vec4(col*mask ,1.0);
}
